hudson.tasks.Shell.command:
#!/bin/bash touch totest.yaml echo "bundle: $BUILD_CHARM_TARGET" >> totest.yaml echo "bundle_name: $CHARM_NAME" >> totest.yaml juju switch $CONTROLLER
# we have to figure out which credential to use because of: # https://bugs.launchpad.net/juju/+bug/1652171 cloud=$(juju add-model wont-be-added --credential invalid-credential 2>&1 | sed -e 's/.*cloud "\?\([^ "]*\)"\?.*/\1/') credential_arg="" if [[ "$cloud" != "localhost" && "$cloud" != "lxd" ]]; then if ! juju credentials --format=json | grep -q $cloud; then echo 'This cloud requires a credential which was not found.' echo 'Please use set-credentials to add the credential.' exit 1 fi credential=$(juju credentials --format=json | jq -r '.credentials.'${cloud}'."cloud-credentials" | keys[0]') credential_arg="--credential=$credential" fi juju add-model test-{{charmname}}-$BUILD_NUMBER $credential_arg function cleanup_model() { juju destroy-model test-{{charmname}}-$BUILD_NUMBER -y } trap cleanup_model EXIT
env MATRIX_OUTPUT_DIR=/srv/artifacts/{{charm_fname}}_in_{{bundle_fname}}/$BUILD_NUMBER cwr -F $CONTROLLER:test-cwr-$CHARM_NAME-$BUILD_NUMBER totest.yaml --results-dir /srv/artifacts --test-id $BUILD_NUMBER
